CREATE TABLE `pastep`.`users` ( `id` INT NOT NULL AUTO_INCREMENT , `username` VARCHAR(200) NOT NULL , `password` TEXT NOT NULL , `email` VARCHAR(200) NOT NULL , `tmp_password` TEXT NOT NULL , `is_verified` BOOLEAN NOT NULL DEFAULT FALSE , `verification_code` VARCHAR(200) NOT NULL , `token` VARCHAR(200) NOT NULL , PRIMARY KEY (`id`), UNIQUE `username` (`username`(200)), UNIQUE `email` (`email`(200))) ENGINE = InnoDB;